mapper.select:
select CAR_CATEGORY_ID carCategoryId, CAR_CATEGORY_CODE carCategoryCode, CAR_CATEGORY_NAME carCategoryName, ENABLED_FLAG enabledFlag, START_DATE startDate, END_DATE endDate, CREATED_BY createdBy, CREATION_DATE creationDate, LAST_UPDATED_BY lastUpdatedBy, LAST_UPDATE_DATE lastUpdateDate, LAST_UPDATE_LOGIN lastUpdateLogin, ATTRIBUTE1 attribute1, ATTRIBUTE2 attribute2, ATTRIBUTE3 attribute3, ATTRIBUTE4 attribute4, ATTRIBUTE5 attribute5, ATTRIBUTE6 attribute6, ATTRIBUTE7 attribute7, ATTRIBUTE8 attribute8, ATTRIBUTE9 attribute9, ATTRIBUTE10 attribute10, ORG_ID orgId from FMS.FMS_CAR_CATEGORY where 1=1
AND CAR_CATEGORY_CODE = #{carCategoryCode}%
AND CAR_CATEGORY_NAME = #{carCategoryName}

select count(*)
from FMS.FMS_CAR_CATEGORY
where CAR_CATEGORY_CODE = #{carCategoryCode}

  and CAR_CATEGORY_ID != #{carCategoryId}
